mapper.update:
UPDATE cardimage
identity_card_image_url=#{identityCardImageUrl}, image_url=#{imageUrl}, status=#{status},
WHERE user_id=#{userId}

mapper.select:
select c.id as id ,c.user_id as userId,c.identity_card_image_url as identityCardImageUrl , c.image_url as imageUrl,c.status as status,u.name as name,r.role_name rolename,r.id as roleId from user u , cardimage c,user_role ur,role r where u.id=c.user_id and u.id=ur.user_id and ur.role_id=r.id
and u.name like '%' #{name} '%'
and r.id = #{roleId}
and c.status = #{status}
