mapper.select:
select j.id,j.name,j.type,j.personnum,j.place,j.information,j.salary,j.contact,j.createtime,j.status,u.id as
uid,u.name as uname,phone
from job j,user u where j. user_id =u.id and j.status=0

    and place= #{place}


    and type= #{type}

order by createtime desc

mapper.update:
UPDATE job
name=#{name}, type=#{type}, personnum=#{personnum}, place=#{place}, information=#{information}, salary=#{salary}, contact=#{contact}, createtime=#{createtime}, status=#{status}
where id=#{id}
